import React,{useState} from 'react'

const Practice = () => {
    const[data, setData] = useState({
        name:'',
        email:'',
        number:''
    });
    
    const inputEvent = (event) =>{
        const {name, value} = event.target;
        setData((preValue)=>{
            return{
                ...preValue,
                [name] : value,
            }
           
        });
    }

    const onSubmits = (event) =>{
        event.preventDefault()
        alert('form submited');
    }


    return (
        <div>
        <form onSubmit={onSubmits}>
            <input 
                type="text"
                placeholder="name"
                name="name"
                value={data.name}
                onChange={inputEvent}
            />
            <input 
                type="email"
                placeholder="email"
                name="email"
                value={data.email}
                onChange={inputEvent}
            />
            <input 
                type="number"
                placeholder="number"
                name="number"
                value={data.number}
                onChange={inputEvent}
            />
            </form>
        </div>
    )
}

export default Practice
