import React,{useState} from 'react';
import './index.css';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"; // bootstrap

const Todo = () => {

    const[inputData, setInputData] = useState('');  // for input item
    const[items, setItems] = useState([]);  // for add items

    // Add items
    const addItem = () => {
        if(!inputData) {}
        else{
            const allInputData = {id: new Date().getTime().toString(), name: inputData}
            setItems([...items, allInputData]);
            setInputData('');
        }
    }

    //delete Items
    const deleteItem = (index) => {
        const updateItems = items.filter((elem) => {
            return index !== elem.id;
        });
        setItems(updateItems);
    }

    // Edit Items
    // When use click on edit button

    // 1. Get the id and name of the data which user clicked to edit
    // 2. Set the toggle mode to change the submit button into edit button
    // 3. Now update the value of the setInput with the new updated value to edit.
    // 4. To pass the current element Id to new state variable for reference

    const editItem = () =>{

    }

    

    //Remove All Items
    const removeAll = () =>{
        setItems([]);
    }

    return (
        <>
            <div className="main_div">
                <div className="child_div">
                <h1 className="text-uppercase">Add Your List Here</h1>
                {/* Add Items */}
                <div className="addItems"> 
                    <input 
                    type="text" 
                    placeholder="Add Items..." 
                    className="form-control  mt-4"
                    value={inputData}
                    onChange={(e) => setInputData(e.target.value)}
                     />
                     <i className="fa fa-plus add_btn" 
                     title="Add Item"
                     onClick={addItem}
                     ></i>
                </div>

                {/* Show Items */}
                <div className="showItems">

                {
                    items.map((elem) => {
                        return(
                            <>
                                <div className="eachItems" key={elem.id}>
                                  <h3 className="mt-5">{elem.name}</h3>
                                  <div className="todo_btn">
                                    <i className="fa fa-pencil-square-o edit_btn" title="Edit Item"
                                    onClick={()=>editItem(elem.id)}></i>
                                    <i className="fa fa-trash add_btn" title="Delete Item"
                                    onClick={()=>deleteItem(elem.id)}></i>
                                  </div>
                                  
                                </div>
                            </>
                        )
                    })
                }

                </div>

                {/* Clear All List */}
                <div className="showItems">
                    <button 
                    className="btn btn-primary mt-5 w-100 text-uppercase"
                    onClick={removeAll}
                    >Remove All</button>
                </div>


                </div>
            </div>
        </>
    )
}

export default Todo
