import React,{useState} from 'react'

const Practice = () => {

    const [data, setData] = useState({
        name:'',
        email:'',
        contact:''
    });

    const inputEvent = (event) =>{
        const {name, value} = event.target;
        setData((preValue) =>{
            return{
                ...preValue,
                [name]:value,
            }
        })
        setData('');
    }

    const onSubmits = (event) => {  
        event.preventDefault();
        alert(`
            Name: ${data.name}
            Email: ${data.email}
            contact: ${data.contact}

        `);
    }


    return (
        <div>
        <form onSubmit = {onSubmits}>
        <input type="text"
         placeholder="enter your name"
        className="m-5" 
        value={data.name}
        name="name"
        onChange={inputEvent} />

        <input type="email"
                placeholder="Enter Your Email"
                className="m-5" 
                value={data.email}
                name="email"
                onChange={inputEvent} />

        <input type="number"
                placeholder="enter your number"
                className="m-5" 
                value={data.contact}
                name="contact"
                onChange={inputEvent} />

                <button className="btn btn-primary m-5">submit</button>

</form>
                    
        </div>
    )
}

export default Practice
