import React,{useState} from 'react';
import './index.css';
import "../node_modules/bootstrap/dist/css/bootstrap.min.css"; // bootstrap

const Todo = () => {

    const[inputData, setInputData] = useState('');  // for input item
    const[items, setItems] = useState([]);  // for add items

    const addItem = () => {
        if(!inputData) {}
        else{
            setItems([...items, inputData]);
            setInputData('');
        }
     
    }

    return (
        <>
            <div className="main_div">
                <div className="child_div">
                <h1 className="text-uppercase">Add Your List Here</h1>
                {/* Add Items */}
                <div className="addItems"> 
                    <input 
                    type="text" 
                    placeholder="Add Items..." 
                    className="form-control  mt-4"
                    value={inputData}
                    onChange={(e) => setInputData(e.target.value)}
                     />
                     <i className="fa fa-plus add_btn" 
                     title="Add Items"
                     onClick={addItem}
                     ></i>
                </div>

                {/* Show Items */}
                <div className="showItems">
                    <div className="eachItems">
                        <h3>Apple</h3>
                        <i className="fa fa-trash add_btn" title="Delete Items"></i>
                    </div>
                </div>

                {/* Clear All List */}
                <div className="showItems">
                    <button className="btn btn-primary mt-5 w-100 text-uppercase">Check List</button>
                </div>


                </div>
            </div>
        </>
    )
}

export default Todo
